INSERT INTO pragas (nome, periodo_ataque, alimentacao, atracao) VALUES
('Lagarta-do-cartucho', 'Crescimento vegetativo', 'Folhas e vagens do feijão', 'Plantas de flores podem atrair lagartas; evitar a proximidade pode ajudar.'),
('Ácaros', 'Épocas de seca e altas temperaturas', 'Se alimentam das folhas, causando desfolha.', 'O cultivo de plantas hospedeiras nas proximidades pode aumentar a infestação'),
('Mosca-branca', 'Verão', 'Sugas das folhas', 'Plantas próximas que servem como abrigo podem atrair.'),
('Pulgões', 'Início do ciclo', 'Seiva das plantas', 'Plantas com flores atraem predadores naturais que ajudam no controle.'),

('Percevejos', 'Durante o desenvolvimento do grão.', 'Se alimentam dos grãos em formação', 'Plantas adjacentes podem atraí-los; controle integrado é recomendado.'),
('Lagarta-da-espiga', 'Principalmente na fase de espigamento.', 'Atacam as espigas e folhas do arroz.', 'Ervas daninhas nas proximidades podem atrair.'),
('Cochonilhas', 'Durante todo o ano, especialmente em climas quentes.', 'Se alimentam da seiva das folhas e frutos.', 'Plantas vizinhas podem ser hospedeiras; controle deve incluir práticas de manejo.'),
('Lagartas', 'Na fase de brotação.', 'Atacam as folhas e brotos.', 'Plantas adjacentes podem aumentar a infestação.'),

('Traça-da-maçã', 'Principalmente durante a floração e frutificação.', 'As larvas se alimentam da polpa dos frutos, causando danos diretos.', 'Plantas próximas que florescem podem atrair as traças.'),
('Broca-da-cana', 'Durante todo o ciclo, principalmente nas fases vegetativas.', 'As larvas se alimentam do interior dos colmos, causando danos significativos.', 'A presença de cana em áreas vizinhas pode aumentar a infestação.'),
('Lagarta-da-cana', 'Principalmente nas fases de crescimento e brotação.', 'Atacam folhas, causando desfolha.', 'Ervas daninhas e plantas hospedeiras próximas podem aumentar a infestação.'),
('Bicudo-da-Cana', 'Principalmente durante o início do desenvolvimento das plantas, mas pode ocorrer ao longo de todo o ciclo.', 'Os adultos perfuram o rizoma e as raízes, enquanto as larvas consomem os tecidos internos, causando redução no crescimento e na produtividade.', 'Áreas com resíduos de colmos e restos de culturas de cana de safras anteriores favorecem o aumento da infestação.'),

('Mosca-dos-estábulos', 'Durante a época quente do ano.', 'Picam os animais, causando desconforto e estresse.', 'Estábulos e locais com resíduos de animais atraem essas moscas.'),
('Carrapatos', 'Durante todo o ano, especialmente em regiões quentes e úmidas.', 'Se alimentam do sangue dos animais, podendo transmitir doenças.', 'Ambientes com vegetação densa aumentam a infestação.'),
('Besouro da batata', 'Principalmente na fase de crescimento das folhas.', 'Se alimenta das folhas, causando desfolha significativa.', 'Plantas hospedeiras próximas podem aumentar a infestação.'),
('Gorgulho da batata', 'Durante o armazenamento e em solo.', 'As larvas se alimentam dos tubérculos, causando danos graves.', 'Solo e resíduos de batata podem atrair.'),

('Larvas de moscas', 'Em condições úmidas.', 'Infestam feridas abertas.', 'Feridas expostas e resíduos orgânicos.');